mapper.select:
SELECT NVL(COUNT(1), 0) FROM
	(SELECT list.*, ROWNUM AS RNUM
	FROM (

			ORDER BY B_NUM DESC
			) list
		)

mapper.update:
UPDATE SPRING_BOARD SET B_NAME = #{b_name}, B_TITLE = #{b_title}, B_CONTENT = #{b_content}, B_UPDATEDATE = SYSDATE
, B_FILE = #{b_file}
B_NUM = #{b_num} AND B_DELETEYN = 'Y'
